import "./avaliacoes.css";
import firebase from "../../db/firebase";
import { useEffect, useState } from "react";

const Avaliacoes = () => {
  const [avaliacoes, setAvaliacoes] = useState<any>([]);

  const ref = firebase.firestore().collection("avaliacoes");

  useEffect(() => {
    getAvaliacoes();
    console.log("hey!");
  }, []);

  const getAvaliacoes = async () => {
    const data = await ref.get();
    data.docs.forEach((item) => {
      setAvaliacoes([...avaliacoes, item.data()]);
    });
  };

  return (
    <div>
      <div className="cards-2">
        <div className="container">
          <div className="row">
            <div className="col-lg-12">
              <h2>Avaliações</h2>
            </div>
          </div>
          <div className="row">
            <div className="col-lg-12">
              {console.log(avaliacoes)}
              {avaliacoes &&
                avaliacoes.map((avaliacao: any) => {
                  return (
                    <div className="card" key={avaliacao.nome}>
                      <div className="card-image">
                        <i className="fas fa-star"></i>
                        <i className="fas fa-star"></i>
                        <i className="fas fa-star"></i>
                        <i className="fas fa-star"></i>
                        <i className="fas fa-star"></i>
                        <hr className="testimonial-line" />
                      </div>
                      <div className="card-body">
                        <div className="testimonial-text">
                          {avaliacao.avaliacao}
                        </div>
                        <div className="testimonial-author">
                          {avaliacao.nome} - {avaliacao.profissao}
                        </div>
                      </div>
                    </div>
                  );
                })}
            </div>
          </div>
          <div className="row">
            <div className="col-lg-12 mb-5">Deixe a sua também!</div>
            <div className="col-lg-12 mb-5">
              <form className="avaliacoes-form">
                <div className="inputs">
                  <div className="col-lg-6 col-md-6 form-group">
                    <input
                      type="text"
                      className="form-control"
                      id="nome"
                      placeholder="Nome completo"
                    />
                  </div>
                  <div className="col-lg-6 col-md-6 form-group">
                    <input
                      type="profissão"
                      className="form-control"
                      id="profissao"
                      placeholder="Profissão"
                    />
                  </div>
                </div>

                <div className="col-lg-12 col-md-12 form-group">
                  <textarea
                    className="form-control"
                    id="exampleFormControlTextarea1"
                    placeholder="Deixe sua opinião!"
                    rows={8}
                  ></textarea>
                </div>

                <div className="col-lg-12 col-md-12">
                  <button className="btn-solid-lg submit" type="submit">
                    Enviar
                  </button>
                </div>
              </form>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Avaliacoes;
